<style lang="scss">
.footer {
  border-top: #80819133 1px solid;
}

.uic-icon{
  flex-basis: 50%;
}
.uic-name{
  visibility: invisible;
}

.made-by{
   transition: 0.6s ease-in-out;
   transform:translateX(110px);
  &:hover{
  transition: 0.6s ease-in-out;
  transform:translateX(0);
  .uic-name{
    visibility: visible;
    transition: 0.6s ease-in-out;
  }
}
}

// TOOLTIP for socials
.socials__link{

  &:hover {
    .socials-tooltip{
    opacity:1;
  }
  }
  .socials-tooltip{
    width: 70px;
    height: 25px;
    position:absolute;
    top:-35px;
    left:0;
    background-color: #373A43;
    font-size: 13px;
    opacity:0;
    border-radius: 5px;
    display: flex;
    justify-content: center;
    align-items: center;
    color:white;
    pointer-events: none;

    &::before{
      position:absolute;
      content:"";
      background-color: #373A43;
      height:10px;
      width:10px;
      bottom:-5px;
      left:25%;
      transform: translateX(-50%) rotate(45deg);
}
  }
}


@media screen and (min-width: 370px) and (max-width: 767px) {
  .footer-top,
  .footer-bottom {
    display: block;
  }
  .footer__links a {
    line-height: 24px;
  }
  .footer-socials{
    margin-top: 40px;
  }
  .reserved,
  .footer-socials,
  .made-by {
    margin-bottom: 15px;
  }
  .vertical-stick {
    width: 0;
  }
  .made-by{
  transform:translateX(0);
}
}
@media screen and (min-width: 370px) and (max-width: 640px) {
  .hero-socials__link {
    width: 24px;
    height: 24px;
    display: flex;
    justify-content: center;
    align-items: center;
  }

}
</style>
